StatisticCard.subvalue:
{= ${
						path: 'planModel>Header/PlanningEnabled'
					} ? ${
						path: 'planModel>Header/PlanningBeginDate',
						type: 'sap.ui.model.type.Date',
						formatOptions: {
							pattern: 'dd MMM YYYY'
						}
					}.concat(' - ')
					.concat(${
							path: 'planModel>Header/PlanningEndDate',
							type: 'sap.ui.model.type.Date',
							formatOptions: {
								pattern: 'dd MMM YYYY'
							}
						}) : null
				}

StatisticCard.value:
{= 
					${
						path: 'planModel>Header/QuotaAccrualBeginDate',
						type: 'sap.ui.model.type.Date',
						formatOptions: {
							pattern: 'dd MMM YYYY'
						}
					}.concat(' - ')
					.concat(${
							path: 'planModel>Header/QuotaAccrualEndDate',
							type: 'sap.ui.model.type.Date',
							formatOptions: {
								pattern: 'dd MMM YYYY'
							}
						})
				}

{
					path: 'planModel>Header/QuotaAvailable',
					type: 'sap.ui.model.odata.type.Decimal',
					formatOptions: {
						minFractionDigits: 0,
						maxFractionDigits: 2,
						groupingEnabled: false,
						decimalSeparator: ','
					}
				}

{
					path: 'planModel>Header/QuotaToBePlanned',
					type: 'sap.ui.model.odata.type.Decimal',
					formatOptions: {
						minFractionDigits: 0,
